var styleTag = document.createElement('style');
styleTag.innerHTML = `
  body, html {
    width: 100%;
    height: 100%;
    background: #000;
    padding: 0;
    margin: 0;
    overflow-x: hidden;
    overflow-y: hidden;
  }

  * {
    margin: 0;
    border: 0;
    padding: 0;
    text-decoration: none;
  }
  
  .player-container {
    position: relative;
    width: 100%;
    height: calc(100vh - 50px);
    z-index: 1;
  }

  .prism-player {
    width: 100%;
    height: 100%;
  }

  .button-container {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    display: flex;
    justify-content: center;
    padding-bottom: 20px;
    z-index: 2;
  }

  .button {
    display: inline-block;
    height: 35px;
    background-color: skyblue; 
    color: white;
    text-align: center;
    line-height: 35px;
    border-radius: 25px;
    padding: 0 20px;
    text-decoration: none;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    transition: background-color 0.3s;
  }

  .button:hover {
    background-color: deepskyblue; 
  }
`;

document.head.appendChild(styleTag);
